import React, { useState, useEffect } from "react";
import Header from "./Header";
import SearchBar from "./SearchBar";
import RandomButton from "./RandomButton";
import PlaneteersContainer from "./PlaneteersContainer";
import "./index.css";

function App() {
  const [planeteers, setPlaneteers] = useState([]);
  const [filteredPlaneteers, setFilteredPlaneteers] = useState([]);

  useEffect(() => {
    fetch("https://planeteers-api.herokuapp.com/planeteers")
      .then((res) => res.json())
      .then((data) => {
        setPlaneteers(data);
        setFilteredPlaneteers(data);
      });
  }, []);

  const handleSearch = (searchTerm) => {
    const lowerCaseSearchTerm = searchTerm.toLowerCase();
    setFilteredPlaneteers(
      planeteers.filter(
        (planeteer) =>
          planeteer.name.toLowerCase().includes(lowerCaseSearchTerm) ||
          planeteer.bio.toLowerCase().includes(lowerCaseSearchTerm) ||
          planeteer.twitter.toLowerCase().includes(lowerCaseSearchTerm)
      )
    );
  };

  const handleRandom = () => {
    const randomIndex = Math.floor(Math.random() * planeteers.length);
    setFilteredPlaneteers([planeteers[randomIndex]]);
  };

  return (
    <div className="App">
      <Header />
      <SearchBar handleSearch={handleSearch} />
      <RandomButton handleRandom={handleRandom} />
      <PlaneteersContainer planeteers={filteredPlaneteers} />
    </div>
  );
}

export default App;
