import React, { useState, useEffect } from "react";
import Header from "./Header";
import SearchBar from "./SearchBar";
import RandomButton from "./RandomButton";
import PlaneteersContainer from "./PlaneteersContainer";
import "/Users/furiousinc/Desktop/cc/src/index.css";

function App() {
  const [planeteers, setPlaneteers] = useState([]);
  const [filteredPlaneteers, setFilteredPlaneteers] = useState([]);
  const [searchTerm, setSearchTerm] = useState("");

  useEffect(() => {
    fetch("http://localhost:8003/planeteers")
      .then((res) => res.json())
      .then((data) => {
        setPlaneteers(data);
        setFilteredPlaneteers(data);
      });
  }, []);

  useEffect(() => {
    setFilteredPlaneteers(
      searchTerm
        ? planeteers.filter((planeteer) =>
            [planeteer.name, planeteer.bio, planeteer.twitter].join(" ")
              .toLowerCase()
              .includes(searchTerm.toLowerCase())
          )
        : planeteers
    );
  }, [searchTerm, planeteers]);

  const handleSearch = (searchTerm) => {
    setSearchTerm(searchTerm);
  };

  const handleRandom = () => {
    const randomIndex = Math.floor(Math.random() * planeteers.length);
    setFilteredPlaneteers([planeteers[randomIndex]]);
  };

  return (
    <div className="App">
      <Header />
      <SearchBar handleSearch={handleSearch} />
      <RandomButton handleRandom={handleRandom} />
      <PlaneteersContainer planeteers={filteredPlaneteers} />
    </div>
  );
}

export default App;
