const SearchBar = (props) => {
  const { searchTerm, setSearchTerm, handleSort } = props;

  const handleChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handleSortChange = (event) => {
    handleSort(event.target.checked);
  };

  return (
    <div className="SearchBar">
      <input
        type="text"
        placeholder="Search for a planeteer"
        value={searchTerm}
        onChange={handleChange}
      />
      <label>
        Sort by Age:
        <input type="checkbox" onChange={handleSortChange} />
      </label>
    </div>
  );
};

const App = () => {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortByAge, setSortByAge] = useState(false);
  const [planeteers, setPlaneteers] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8003/planeteers")
      .then((res) => res.json())
      .then((data) => {
        setPlaneteers(data);
      });
  }, []);

  const filteredPlaneteers = planeteers.filter((planeteer) => {
    return (
      planeteer.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      planeteer.bio.toLowerCase().includes(searchTerm.toLowerCase())
    );
  });

  const sortedPlaneteers = filteredPlaneteers.sort((a, b) => {
    if (sortByAge) {
      return a.birthYear - b.birthYear;
    } else {
      return a.id - b.id;
    }
  });

  const handleSort = (sortByAge) => {
    setSortByAge(sortByAge);
  };

  return (
    <div className="App">
      <Header />
      <SearchBar
        searchTerm={searchTerm}
        setSearchTerm={setSearchTerm}
        handleSort={handleSort}
      />
      <RandomButton />
      <PlaneteersContainer planeteers={sortedPlaneteers} />
    </div>
  );
};
